import styled, {keyframes} from "styled-components";

export const HeaderWrapper = styled.div`
    border: 1px solid;
    background: #ee4d2d;
`
export const HeaderTopWrapper = styled.div`
    width: 100%;
    display: flex;
    justify-content: space-between;
    align-items: center;
    .logo {
        width: 12%;
        display: inline-block;

        & > img {
            width: 100%;
        }
    }
    .header-top-right {
        width: 80%;
        display: flex;
        flex-wrap: wrap;
        & > .input {
            width: 70%;
        }
        & > .account {
            display: flex;
            align-items: center;
        }
    }

`
