import React, { useEffect, useState } from "react";
import { FootwearWrapper } from "./style";
import { connect } from "react-redux";
import { ProductFootWear } from "./data";
import { handleInitProduct } from "./action";
import ProductItem from "../Home/Component/ProductItem";
import ReactPaginate from 'react-paginate';
const Footwear = props => {
    const {handleInitProduct, products} = props;
    const {product} = products
    const [page, setPage] = useState(product.slice(0,10));
    const [pageNumber, setPageNumber] = useState(0);
    const productsPerPage = 10;
    
    console.log('page', page);
    console.log('12', products);
    useEffect(() => {
        handleInitProduct(ProductFootWear)
    },[]);
    return(
        <FootwearWrapper className="list-product">
            <div className="container">
                <div className="row">
                    {product.map(item => {
                        return <ProductItem item={item} key={item.id} className="col-lg-3 col-md-6 product-item"/>
                    })}
                </div>
            </div>
        </FootwearWrapper>
    );
}
const mapStateToProps = state => {
    return {
        products: state. productFootWaer
    }
}
const mapDispatchToProps = dispatch => {
    return {
        handleInitProduct: (data) => dispatch(handleInitProduct(data))
    }
}
export default connect(mapStateToProps, mapDispatchToProps) (Footwear);
