import React, { useEffect, useState } from "react";
import Buttons from "../Button";
import Input from './../input'
import { HeaderTopWrapper, HeaderWrapper } from "./style";
import _ from 'lodash';
import { NavLink } from "react-router-dom";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faUser } from '@fortawesome/free-regular-svg-icons';
import { faShoppingCart } from '@fortawesome/free-solid-svg-icons';
import { headerTopBottoms } from './data';
const Header = props => {
    const [headerBottom, setHeaderBottom] = useState([]);
    useEffect(() => {
        setHeaderBottom(headerTopBottoms)
    },[]);
    return (
        <HeaderWrapper>
            <div className="container">
                <HeaderTopWrapper>
                    <NavLink to="/" exact className="logo">
                        <img src="/assets/images/logo/5.png" alt="logo" />
                    </NavLink>
                    <div className="header-top-right">
                        <Input
                            type="text"
                            className="input-search"
                            placeholder="Tìm tên sản phẩm mà bạn mong muốn..."
                        />
                        <div className="account-container">
                            <FontAwesomeIcon icon={faUser} />
                            <NavLink
                                to="/dang-nhap"
                                exact
                                className="account-link"
                            >
                                <span className="account-login-logout">
                                    Đăng nhập / đăng ký
                                </span>
                                <span className="account">
                                    Tài khoản
                                </span>
                            </NavLink>

                        </div>
                        <NavLink
                            to="gio-hang"
                            exact
                            className="cart">
                            <span className="cart-icon">
                                <FontAwesomeIcon icon={faShoppingCart} />
                            </span>
                            <span className="cart-text">
                                Giỏ hàng
                            </span>
                        </NavLink>
                    </div>
                </HeaderTopWrapper>
            </div>
        </HeaderWrapper>
    );
}
export default Header;
