import React, { useEffect, useState } from 'react';
import { HomeWrapper, BannerWrapper } from './style';
import Slider from 'react-slick';
import "slick-carousel/slick/slick.css"; 
import "slick-carousel/slick/slick-theme.css";
import { SliderItem } from './data';
import BannerItem from './Component/BannerSlider';
import Buttons from '../../Component/Button';
const Home = props => {
    const [state, setState] = useState({
        slider: [],
    })
    console.log('slider', state.slider);
    useEffect(() => {
        setState({
            ...state,
            slider: SliderItem 
        })
    },[]);
    // xử lý carousel
    const settings = {
      dots: true,
      fade: true,
      infinite: true,
      speed: 500,
      slidesToShow: 1,
      slidesToScroll: 1,
      autoplay: true,
      speed: 500,
      autoplaySpeed: 2000,
      cssEase: "linear"
    }
    const listSelectMenu = ['giày','dép','áo','quần','balo','túi xách','áo nam','áo nữ','phụ kiện khác']
    return(
        <HomeWrapper className="home">
            <div className="container">
                {/* xử lý banner */}
                <BannerWrapper className="banner">
                    <Slider {...settings}>
                        {state.slider.map((item, index) => {
                            return <BannerItem item={item} key={index}/>
                        })}
                    </Slider>
                </BannerWrapper>
                <div className="row">
                    <div className="select-menu col-lg-6 col-md-12">
                        {listSelectMenu.map((item, index) => {
                            return <Button name={item} key={index}/>
                        })}
                    </div>
                </div>
            </div>

        </HomeWrapper>
    );
}
export default Home;
