import styled from "styled-components";
export const HeaderBottomItems = styled.li`
    position: relative;
    cursor: pointer;
    z-index: 5;
    transition:  .3s;

    .item-link {
        display: block;
        padding: 0.75rem 1rem;
        color: ${props => props.activeColor.color};
        font-size: 1rem;
        font-weight: 700;
        text-transform: uppercase;
        transition: .3s;
        transition: all .3s linear;
        @media (max-width: 1200px) {
            font-size: .9rem;
        }
        @media (max-width: 1075px) {
            font-size: .8rem;
        }


        &:hover {
        color: #ee4d2d;

    }
        @media (max-width: 992px) {
        display: flex;
        justify-content: space-between;
        }

        &.active-menu {
            border-bottom: 3px solid #ee4d2d;
            color: #ee4d2d;
            font-size: 1.2rem;
        }
        & > svg {
            margin-left: .3rem;
        }
    }
    @media (max-width: 992px) {
        width: 100%;
        border-bottom: 1px solid #ddd;
    }
`
export const SubMenuWrapper = styled.ul`

`
