import styled from 'styled-components';
export const DetailWrapper = styled.div`

`
export const ProductDetailWrapper = styled.div`
  display: flex;
  width: 100%;
  .slider-images,.select-delivery {
    width: 25%;
    padding: 1.4rem 1rem;
    height: 300px;
    border: 1px solid;
  }
  .select-product {
    flex: 1;
    border: 1px solid;
  }
`
