import React from "react";
import { ProductCartItems } from "./style";
import Input from "./../../../../Component/input"
const ProductCartItem = ({
  productCartItem
}) => {
  const {
    color,
    name,
    price,
    quantity,
    size,
    avatar
  } = productCartItem;
  console.log(size);
  return(
    <ProductCartItems>
      <tr>
      <td>
        <Input
        type="checkbox"
        name="check"
        />
      </td>
      <td>
        <img src={avatar}/>
      </td>
      <td>
        <h4 className="name-product">
          {name}
        </h4>
        <p className="info">
          Màu: {color}, {size && `Size: ${size}`}
        </p>
      </td>
      <td>
        <h4 className="name-product">
          {name}
        </h4>
        <p className="info">
          Màu: {color}, {size && `Size: ${size}`}
        </p>
      </td>
      <td>
        <h4 className="name-product">
          {name}
        </h4>
        <p className="info">
          Màu: {color}, {size && `Size: ${size}`}
        </p>
      </td>
      <td>
        <h4 className="name-product">
          {name}
        </h4>
        <p className="info">
          Màu: {color}, {size && `Size: ${size}`}
        </p>
      </td>
      </tr>
    </ProductCartItems>
  );
}
export default ProductCartItem;
