import styled from 'styled-components';
export const ChangeColor = styled.div`
    width: 20%;
    position: fixed;
    top: 40%;
    right: 0;
    z-index: 999999;
    text-align: right;
    .icon-setting {
      font-size: 2rem;
      margin: .75rem .35rem;
      cursor: pointer;
      position: relative;
      z-index: 999999;
      & > svg {
      transform: rotate(0deg);
      --ms-transform: rotate(0);
      --moz-transform: rotate(0);
      --o-transform: rotate(0);
      --webkit-transform: rotate(0);
      transition: all .3s linear;
      }
      &.change-setting > svg {
        color: #fff;
        transform: rotate(120deg);
        --ms-transform: rotate(360deg);
        --moz-transform: rotate(360deg);
        --o-transform: rotate(360deg);
        --webkit-transform: rotate(360deg);
      }
    }
    .blur-layout {
      width: 100%;
      height: 100vh;
      position: fixed;
      top: 0;
      left: 0;
      z-index: 9999;
      background: rgba(0,0,0,.5);
      cursor: pointer;
      opacity: 0;
      visibility: hidden;
      transition: .3s;
      &.change-blur-layout {
        opacity: 1;
        visibility: visible;
      }
    }
    .list-color {
      background: #fff;
      width: 100%;
      height: 0;
      overflow: hidden;
      display: flex;
      flex-wrap: wrap;
      position: absolute;
      top: 100%;
      left: 0;
      /* transform: translate(-.75rem, .35rem); */
      margin: .25rem -.35rem;
      border-radius: 5px;
      box-shadow: rgba(149, 157, 165, 0.2) 0px 8px 24px;
      transition: all .3s linear;
      &.show-list-color {
        height: auto;
      }
    }
`
