import styled from "styled-components";
export const PostCareItems = styled.div`
  padding: 1.2rem 0;
  border-bottom: 1px solid #cacaca;
  display: flex;

  & > img {
    width: 30%;
    height: fit-content;
  }
  & > .info {
    flex: 1;
    padding: 0 1rem;
  }

`
