import styled from "styled-components";
export const SellingItems = styled.div`
  padding: 0.15rem .35rem 2rem;
  display: flex;
  flex-direction: column;
  flex-wrap: wrap;
  margin-bottom: 2rem;
  border-bottom: 1px solid
 #cacaca;
  & > img {
    width: 100%;
    transition: .3s;
    transform: scale(.9);
    cursor: pointer;
    &:hover {
      transform: scale(1);
    }
  }
  .title-product {
    font-size: .95rem;
    margin: .55rem 0;
    max-width: 100%;
    min-height: 20px;
    white-space: pre-wrap;
    overflow: hidden;
    -webkit-line-clamp: 2;
    -webkit-box-orient: vertical;
    display: -webkit-box;
    text-overflow: ellipsis;
    cursor: pointer;
  }
  .price {
    color: #ee4d2d;
    font-size: 1.3rem;
  }
  .discount {
    color: #cacaca;
    &:after {
      content: '${props => props.discountContent ? props.discountContent + '%' : ''}';
      text-decoration: none;
      color: #000;
      margin-left: .45rem;
    }
    &:before {
      content: '${props => props.formatDiscount ? props.formatDiscount : ''}';
      text-decoration: none;
      color: #000;
      text-decoration: line-through;
      color: #cacaca;
      margin-right: .35rem;
    }
  }
`;
