import styled from "styled-components";
export const SubMenuItems = styled.li`
  & > a {
    color: #000;
    padding: 0.45rem 1rem;
    display: block;
    border-bottom: 1px solid #ddd;
    text-transform: uppercase;
    position: relative;
    &.active-menu {
      color: #ee4d2d;
    }
    &:hover {
      color: #ee4d2d;
    }
  }
`
