import {
  call,
  takeLatest,
  takeEvery,
  put,
  all,
  fork
} from "redux-saga/effects";
import {
  getListProductDetailSuccess,
  getListProductDetailFailed,
  getListProductSuccess,
  getListProductFailed
} from "./action";
import {getListProductDetailApi, getListProductInvolveApi} from "./service";
import {GET_LIST_PRODUCT_DETAIL_REQUEST, GET_LIST_PRODUCT_REQUEST} from './contants';
function* getListProductDetailProcess(params) {
  try {
    const {data: id,type} = params;
    const responDetail = yield call(getListProductDetailApi, id);
    const responProduct = yield call(getListProductInvolveApi);
    if(responDetail.status === 200) {
      yield put(getListProductDetailSuccess(respon.data))
    }else {
      yield put(getListProductDetailFailed())
    }
  }catch (err) {
    yield put(getListProductDetailFailed(err))
  }
}
function* watchGetListProductDetail() {
  yield takeLatest(GET_LIST_PRODUCT_DETAIL_REQUEST, getListProductDetailProcess)
}

function* getListProductProcess(params) {
  try {
    const respon = yield call(getListProductInvolveApi);
    console.log('bbbbbbbb', respon);
  }catch (err) {
    console.log('aaaaaaaaaaaaaaaa');
  }
}
function* watchGetListProduct() {
  yield takeLatest(GET_LIST_PRODUCT_REQUEST,getListProductProcess);
}
function* watchAll() {
  yield all([
    watchGetListProductDetail(),
    // watchGetListProduct()
  ])
}

export default watchAll;
