import React from 'react';
import { SelectOptionWrapper } from './style';
const SelectOption = props => {
    const { option, nameSort, nameSelect } = props;
    return (
        <React.Fragment>
            <label htmlFor={nameSelect}>{nameSort}</label>
            <SelectOptionWrapper value="" id={nameSelect}>
                {option.map(item => {
                    return <option value={item.id}>
                        {item.name}
                    </option>
                })}
            </SelectOptionWrapper>
        </React.Fragment>
    );
}
export default SelectOption;
