import React, { useEffect } from "react";
import { CartWrapper } from "./style";
import {
  getListProductCartRequest
} from "./actions";
import { connect } from "react-redux";
import {
  selectCart,
  selectIsLoading
} from "./selector";
import Loading from "../Components/Loading";
import Empty from "../Components/Empty";
import { NavLink } from "react-router-dom";

const Cart = props => {
  const {
    handleGetListProductCart,
    productsCart,
    isLoading,
    history
  } = props;
  useEffect(() => {
    handleGetListProductCart();
  }, [])
  if(isLoading) return <Loading />
  console.log('product', productsCart);
  if(!productsCart.length) return <Empty history={history}/>
  const {
    color,
    name,
    price,
    quantity,
    size
  } = productsCart;
  return(
    <CartWrapper className="cart">
      <div className="container">
        <div className="list-product-cart">
          <NavLink exact to="/" className="link-to">
            Shop thời trang panda >
          </NavLink>
        </div>
        <div className="payment">

        </div>
      </div>

    </CartWrapper>
  );
}

const mapStateToProps = state => {
  return {
    productsCart: selectCart(state),
    isLoading: selectIsLoading(state)
  }
}
const mapDispatchToProps = dispatch => {
  return {
    handleGetListProductCart: () => dispatch(getListProductCartRequest())
  }
}
export default connect(mapStateToProps, mapDispatchToProps) (Cart);
