import styled from "styled-components";
export const SellingItems = styled.div`
padding: .15rem;
display: flex;
flex-wrap: wrap;
  .avatar {
    width: 40%;
    border: 1px solid #ddd;
    padding: .15rem;
    margin-right: .35rem;
    &:hover > img {
      transform: scale(1);
    }
    & > img {
      width: 100%;
      cursor: pointer;
      transition: all .3s ease-in-out;
      transform: scale(0.8);

    }
  }
  & > .info {
    flex: 1;
    .title-product {
      font-size: 1.2rem;
    }
    .money {
      display: flex;
      flex-direction: column;
      .price {
        &:before {
          content: 'đ'
        }
      }
    }
  }
`;
