import styled from "styled-components";
export const HeaderBottomItems = styled.li`
  
    .item-link {
        display: block;
        padding: 0.75rem 1rem;
        color: #000;
        font-size: 1rem;
        font-weight: 700;
        text-transform: uppercase;
        &.active-menu {
            border-bottom: 2px solid #f89537;
            color: #f89537;
            font-size: 1.2rem;
        }
    }
`
