import styled from "styled-components";
export const CartWrapper = styled.div`
  padding: 3rem;
  & > .container {
    display: flex;
    .payment {
      width: 30%;
      padding: 1rem;
      margin: 0 1rem;
      box-shadow: rgb(100 100 111 / 20%) 0px 7px 29px 0px;
      .map {
        border-bottom: 1px solid #cacaca;
      }
      .order-info {
        margin: 1rem 0;
        .info-item {
          display: flex;
          justify-content: space-between;
          align-items: center;
          .price {
            color: #ee4d2d;
          }
          .btn-apply {
            /* flex: 1; */
            margin: 0 1rem;
          }
        }

      }
    }
    .list-product-cart {
      box-shadow: rgb(100 100 111 / 20%) 0px 7px 29px 0px;
      flex: 1;
      .link-to {
        display: inline-block;
        padding: 1rem;
        border-bottom: 1px solid #cacaca;
        width: 100%;
        color: #ee4d2d;
        text-transform: uppercase;
      }
      & > table th,& > table td {
        border-bottom: 1px solid #cacaca;
      }
    }
  }
`;
