import styled, { keyframes } from "styled-components";

export const HeaderWrapper = styled.div`
    border: 1px solid;
    background: #ee4d2d;
`
export const HeaderTopWrapper = styled.div`
    width: 100%;
    display: flex;
    justify-content: space-between;
    align-items: center;
    .logo {
        width: 12%;
        display: inline-block;

        & > img {
            width: 100%;
        }
    }
    .header-top-right {
        width: 85%;
        display: flex;
        flex-wrap: wrap;
        & > .input {
            width: 60%;
            position: relative;
            &:after {
                position: absolute;
                top: 0;
                right: 0;
                width: 100%;
                height: 100%;
                content: 'a';
                display: flex;
                align-items: center;
            }
        }
        & > .account-container {
            display: flex;
            align-items: center;
            color: #fff;
            margin-left: 1rem;
            & > svg {
                cursor: pointer;
                font-size: 2rem;
                margin-right: .65rem;
            }
            .account-link {
                color: #fff;
                font-size: .85rem;
                text-transform: uppercase;
                display: flex;
                flex-direction: column;
                .account {
                    text-transform: capitalize;
                    &:after {
                        content: '\f107';
                        font-family: Fontawesome;
                        margin-left: .25rem;

                    }
                }
            }
        }
        .cart {
            color: #fff;
            display: flex;
            align-items: center;
            margin: 0 1rem;
            & > .cart-icon {
                font-size: 2rem;
                margin: 0 .25rem;
                position: relative;
                &:after {
                    content: '0';
                    position: absolute;
                    top: 0;
                    right:-5px;
                    width: 20px;
                    height: 20px;
                    border-radius: 50%;
                    background: rgb(253, 216, 53);
                    font-size: 1rem;
                    display: flex;
                    justify-content: center;
                    align-items: center;
                    color: #000;
                }
            }
        }
        .list-header-top-bottom {
            width: 100%;
            display: flex;
            margin: 0;
        }
    }

`
