import React from 'react';
import { useState } from 'react';
import { Button, Modal, ModalHeader, ModalBody, ModalFooter } from 'reactstrap';
import Buttons from '../../Button';
import Input from './../../../Component/input'
import { ModalWrapper } from './style';
import Facebook from '../Facebook';
import './style.css'
const ModalRegister = props => {
    const { toggle, modal, user } = props;
    const [state, setState] = useState({
        user: '',
        password: '',
        repass: ''
    })
    const handleChangeInput = event => {
        const { name, value } = event.target;
        setState({
            ...state,
            [name]: value
        })
    }
    return (
        <ModalWrapper className="modal">
            <Modal isOpen={modal} toggle={toggle} className="modal-lg modal-box">
                <div className="login-modal">
                    <h3 className="title-login">
                        Đăng Nhập
                    </h3>
                    <form>
                        <Input className="form-control" nameLabel="Tên tài khoản hoặc địa chỉ email *" type="text" name="user" id="user" value={state.user} onChange={handleChangeInput} placeholder="Tên Đăng Nhập" />
                        <Input className="form-control" nameLabel="Mật khẩu *" type="password" name="password" id="password" value={state.password} onChange={handleChangeInput} placeholder="Mật khẩu" />
                        <div className="login-remember-pass">
                            <Input name="login" type="submit" className="btn btn-danger" value="Đăng Nhập" />
                            <Input nameLabel="Ghi nhớ mật khẩu" type="checkbox" name="rememberPass" id="rememberPass" value={null} onChange={handleChangeInput} />
                        </div>
                        <span className="link-pass">
                            Quên mật khẩu
                        </span>
                        <p>Bạn có thể đăng nhập bằng:</p>
                        <Facebook className="facebook" />
                    </form>

                </div>
                <div className="register-modal">
                    <h3 className="title-register">
                        Đăng Ký
                    </h3>
                    <form>
                        <Input className="form-control" nameLabel="Tên tài khoản hoặc địa chỉ email *" type="text" name="user" id="user" value={state.user} onChange={handleChangeInput} placeholder="Tên Đăng Nhập" />
                        <Input className="form-control" nameLabel="Mật khẩu *" type="password" name="password" id="password" value={state.password} onChange={handleChangeInput} placeholder="Mật khẩu" />
                        <Input className="form-control" nameLabel="Mật khẩu *" type="password" name="repass" id="repass" value={state.repass} onChange={handleChangeInput} placeholder="Nhập lại mật khẩu" />
                        <Input name="register" value="Đăng Ký" className="btn btn-danger" type="submit" />
                    </form>
                </div>
            </Modal>
        </ModalWrapper>
    );
}
export default ModalRegister;
