import React, { useEffect, useState } from "react";
import Buttons from "../Button";
import Input from './../input'
import { HeaderWrapper, HeaderBottomWrapper, CardMobile } from "./style";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faSearch, faTimes } from "@fortawesome/free-solid-svg-icons"
import { listMenu } from "./data";
import HeaderBottomItem from "./headerBottomItem";
import IconCard from "./iconCard";
import { Button, Modal, ModalHeader, ModalBody, ModalFooter } from 'reactstrap';
const Header = props => {
    // Quản lý trạng thái modal
    const [modal, setModal] = useState(false);
    // thay đổi trạng thái modal
    const toggle = () => setModal(!modal);


    //Quản lý trạng thái: input search , các item menu, trạng thái của icon menu hoặc tab menu ở màn mobile
    const [state, setState] = useState({
        search: '',
        listMenus: [],
        status: false
    })
    // Quản
    const [statusCard, setstatusCard] = useState(false)
    useEffect(() => {
        // lấy data
        setState({
            ...state,
            listMenus: listMenu
        })
    }, [])

    //quản lý trang thái dropdown menu
    const [statusMenuItem, setStatusMenuItem] = useState(true);

    //các dropdown menu
    const handleStatusDropMenuItem = () => {
        setStatusMenuItem(!statusMenuItem);
    }
    //menu cấp 1
    const handleStatusMenuItem = () => {
        setStatusMenuItem(true)
    }

    // quản lý onChange input
    const handleInputChange = event => {
        const { name, value } = event.target;
        setState({
            ...state,
            [name]: value
        })
    }
    // quản lý icon mmenu
    const handleStatus = () => {
        setState({
            ...state,
            status: !state.status
        })
    }
    //
    const handleStatusCard = () => {
        setstatusCard(!statusCard);
    }
    const { listMenus } = state;
    const handleCloseMenu = () => {
        setState({
            ...state,
            status: false
        })
    }
    return (
        <HeaderWrapper className="header">
            <div className="header-top">
                <div className="register-modal">
                    <Button name="Đăng Nhập / Đăng Ký" onClick={toggle} className="login">Đăng Nhập / Đăng Ký</Button>
                    <Modal isOpen={modal} toggle={toggle}>
                        <ModalHeader>Modal title</ModalHeader>
                        <ModalBody>
                            <b>Look at the top right of the page/viewport!</b><br />
                            Lorem ipsum dolor sit amet, consectetur adipisicing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua. Ut enim ad minim veniam, quis nostrud exercitation ullamco laboris nisi ut aliquip ex ea commodo consequat. Duis aute irure dolor in reprehenderit in voluptate velit esse cillum dolore eu fugiat nulla pariatur. Excepteur sint occaecat cupidatat non proident, sunt in culpa qui officia deserunt mollit anim id est laborum.
                        </ModalBody>
                        <ModalFooter>
                            <Button color="primary" onClick={toggle}>Do Something</Button>{' '}
                            <Button color="secondary" onClick={toggle}>Cancel</Button>
                        </ModalFooter>
                    </Modal>
                </div>




                <img src="/assets/images/logo/1.png" alt="" />
                <div className="group-card">
                    <div className="search">
                        <span className="icon-search">
                            <FontAwesomeIcon icon={faSearch} />
                        </span>
                        <div className="input-search">
                            <Input type="text" className="form-control" name="search" value={state.search} onChange={handleInputChange} placeholder="Tìm kiếm..." />
                            <Buttons name={<FontAwesomeIcon icon={faSearch} />} className="btn btn-success btn-search" />
                        </div>
                    </div>
                    <div className="card">
                        <span className="sum-price">
                            giỏ hàng/0đ
                        </span>
                        <IconCard className="icon-card" name="0" />

                        <div className="show-card">
                            <span className="empty">
                                Chưa có sản phẩm trong giỏ hàng.
                            </span>
                        </div>
                    </div>
                    <CardMobile className="card-modile" status={statusCard}>

                        <IconCard className="icon-card" name="0" onClick={handleStatusCard} />

                        <div className="show-card">
                            <span className="close-card" onClick={handleStatusCard}>
                                <FontAwesomeIcon icon={faTimes} />
                            </span>
                            <h2 className="title-card">
                                Giỏ Hàng
                            </h2>
                            <span className="empty">
                                Chưa có sản phẩm trong giỏ hàng.
                            </span>
                        </div>
                    </CardMobile>
                </div>
            </div>
            <HeaderBottomWrapper className="header-bottom" status={state.status}>
                <span className="icon-menu" onClick={handleStatus}>
                    <span className="icon-menu-item"></span>
                </span>
                <ul className="list-menu">
                    <li className="close-menu" onClick={handleCloseMenu}>
                        <FontAwesomeIcon icon={faTimes} />
                    </li>
                    <li className="register">
                        <img src="/assets/images/register/user.png" alt="user" />
                        <Buttons className="btn btn-primary" type="register-moblie" name="Đăng nhập | Đăng ký" />
                    </li>
                    {listMenus.map(item => {
                        return <HeaderBottomItem onClick={item.dropdown.length ? handleStatusDropMenuItem : handleStatusMenuItem} item={item} key={item.id} status={statusMenuItem} />
                    })}
                </ul>
            </HeaderBottomWrapper>
        </HeaderWrapper>
    );
}
export default Header;
