import React, { useEffect } from 'react';
const Loading = props => {
  const [isLoading, setIsLoading] = useState(true);
  useEffect(() => {
    setIsLoading(true);
    setTimeout(() => {
      setIsLoading(false);
    }, 3000);
  },[]);
  if(isLoading) return   <div className="loading" style={{
    width: "100%",
    height: "100vh",
    overflow: 'hidden'
  }}>
    <img
      style={{
        margin: '3rem auto',
        display: 'block',
        width: '50%'
      }}
      src="/assets/images/logo/load.gif"
      alt="loading" />
  </div>
}
export default Loading
