import React, { useState } from "react";
import { ProductCartItems } from "./style";
import Input from "./../../../../Component/input";
import { formatMoney } from "./../../../../Component/Format";
import Buttons from "../../../../Component/Button";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faTrashAlt } from "@fortawesome/free-regular-svg-icons"

const ProductCartItem = ({
  productCartItem
}) => {
  const {
    color,
    name,
    price,
    quantity,
    size,
    avatar,
    brand,
    id
  } = productCartItem;
  const [counter, setCounter] = useState(quantity)
  console.log(size);
  const formatPrice = formatMoney(price)
  return (
    <ProductCartItems className="product-cart-item">
      <img src={avatar} alt="" />
      <div className="info">
        <h4 className="name-product">
          {name}
        </h4>
        <p className="relate-info">
          Thương hiệu: {brand},
          {color && ` Nhóm màu: ${color} `},
          {size && `Size: ${size}`}
        </p>
      </div>
      <div className="price">
        <span className="price-text">
          {formatPrice}đ
        </span>
        <Buttons icon={
          <FontAwesomeIcon icon={faTrashAlt} />
        } />
      </div>
      <div className="quantity">
        <Buttons
          name="-"
          type="counter"

        />
        <span className="quantity-text">
          {counter}
        </span>
        <Buttons
          name="+"
          type="counter"
          className="counter increment"
        />
      </div>
    </ProductCartItems>
  );
}
export default ProductCartItem;
