import styled from "styled-components";
export const DetailWrapper = styled.div``;
export const ProductDetailWrapper = styled.div`
  display: flex;
  width: 100%;
  .slider-images,
  .select-delivery {
    width: 25%;
    padding: 1.2rem 1rem;
  }
  .select-product {
    flex: 1;
    border: 1px solid;
    padding: 1.2rem 1rem;
    .name-product {
      font-size: 1.5rem;
      color: #212121;
    }
    .evaluate {
      width: 100%;
      display: flex;
      justify-content: space-between;
      .star {
        display: flex;
        align-items: center;
        .star-item {
          color: #ffd200;
          margin: 0 0.15rem;
        }
        .evaluate-text {
          color: #1a9cb7;
          cursor: pointer;
        }
      }
      .like {
        font-size: 1.5rem;
        cursor: pointer;
      }
    }
    .brand {
      font-size: 0.9rem;
      .name-brand {
        color: #1a9cb7;
        cursor: pointer;
        &:hover {
          text-decoration: underline;
        }
      }
