  }

  .slider-images {
    & > img {
      width: 100%;
      max-height: 350px;
      cursor: pointer;
      object-fit: cover;
      transform: scale(0.9);
      transition: 0.3s;
      &:hover {
        transform: scale(1);
      }
    }
    .indicator {
      display: flex;
      flex-wrap: wrap;
      padding: 2rem 1rem !important;
      margin: 0;
      border-top: 1px solid #cacaca;
      position: relative;
      .slider-next,
