import {createSelector} from 'reselect';
const selectDetailReducer = state => state.detail;
export const selectIsLoading = createSelector(
  selectDetailReducer,
  state => state.isLoading
)
export const selectDetail = createSelector(
  selectDetailReducer,
  state => state.detail
)
