import styled from 'styled-components';
export const ChangeColor = styled.div`
    position: fixed;
    top: 40%;
    right: 0;
    z-index: 999999;
    .icon-setting {
      font-size: 2rem;
      margin: .75rem .35rem;
      cursor: pointer;
      position: relative;
      z-index: 999999;
      transform: rotate(0);
      transition: all .3s linear;
      &.change-setting > svg {
        color: #fff;
        transform: rotate(360deg);
      }
    }
    .blur-layout {
      width: 100%;
      height: 100vh;
      position: fixed;
      top: 0;
      left: 0;
      z-index: 9999;
      background: rgba(0,0,0,.5);
      cursor: pointer;
      opacity: 0;
      visibility: hidden;
      transition: .3s;
      &.change-blur-layout {
        opacity: 1;
        visibility: visible;
      }
    }
`
