import React, { useState } from "react";
import { HeaderBottomItems, DropdownMenuWrapper, DropdownMenuitems } from "./style";
import { NavLink } from "react-router-dom";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faAngleDown } from "@fortawesome/free-solid-svg-icons"
const DropdownMenuItem = props => {
    const { item, parent } = props;

    return (
        <DropdownMenuitems className="dropdown-menu-item">
            <NavLink activeClassName="active-drop-menu" className="drop-link-menu" to={`/${parent}/${item}`}>{item}</NavLink>
        </DropdownMenuitems>
    );
}
const HeaderBottomItem = props => {
    const { item } = props;
    const { name, dropdown } = item;
    const { statusDrop, setStatusDrop } = useState(false);
    const handleStatusDrop = () => {
        setStatusDrop();
    }
    return (
        <HeaderBottomItems className="menu-item">
            <NavLink
                exact
                activeClassName="active-menu"
                className="item-link"
                to={item.name === 'trang chủ' ? '/' : `/${name}`} 
                onClick={handleStatusDrop}
                >
                {item.name}
                {dropdown.length ? <FontAwesomeIcon icon={faAngleDown} /> : ''}
                
            </NavLink>
            {dropdown.length ? <DropdownMenuWrapper className="drop-menu" status={statusDrop}>
                {dropdown.map((item, index) => {
                    return <DropdownMenuItem parent={name} item={item} key={index} />
                })}
            </DropdownMenuWrapper> : ''}
        </HeaderBottomItems>
    );
}
export default HeaderBottomItem;
