import styled from "styled-components";
export const PostDetailWrapper = styled.div`
  & > .container {
    display: flex;
  }
`
export const MainPostDetailWrapper = styled.div`
  flex: 1;
  & > img {
    width: 80%;
    display: block;
    margin: 1rem auto;
  }
`
export const LeftPostDetailWrapper = styled.div`
  width: 25%;
`
