import styled from "styled-components";
export const ProductCartItems = styled.div`
  display: flex;
  align-items: flex-start;
  padding: 1rem;
  color: #000;
  border-bottom: 1px solid #cacaca;
  & > img {
    width: 15%;
    object-fit: contain;
  }
  .info-right {
    display: flex;
    height: max-content;
    @media (max-width: 768px) {
      flex-direction: column;
    }
    .info {
      padding: 0 1rem;
      max-width: 60%;
      display: flex;
      flex-direction: column;
      justify-content: space-between;
      @media (max-width: 768px) {
      flex-direction: column;
    }
      & > .name-product {
        max-width: 100%;
        font-size: 1.2rem;
        font-weight: 500;
        min-height: 50px;
        overflow: hidden;
        text-overflow: ellipsis;
        -webkit-line-clamp: 3;
        -webkit-box-orient: vertical;
        display: -webkit-box;
      }
      & > .relate-info {
        color: #757575;
        line-height: 1;
        font-size: 12px;
      }
    }
    & > .quantity {
      display: flex;
      height: max-content;
      padding: 0 1rem;
      .quantity-text {
        padding: 0.25rem 0.55rem;
      }
    }
    & > .price-text {
      display: block;
      margin: 0 1.5rem;
      color: #ee4d2d;
      font-size: 1.3rem;
    }
    & > .btn-delete {
      display: flex;
      align-items: flex-start;
      color: #ee4d2d;
      font-size: 1.3rem;
      & > svg {
        margin-right: 0.35rem;
      }
    }
  }
`;
