import React, { useEffect, useState } from 'react';
import { HomeWrapper, BannerWrapper, ListProductWrapper, TabItems , CarouselProductWrapper,  BannerDiscountWrapper} from './style';
import Slider from 'react-slick';
import "slick-carousel/slick/slick.css";
import "slick-carousel/slick/slick-theme.css";
import { SliderItem, ListInnerProduct, ProductTabs } from './data';
import BannerItem from './Component/BannerSlider';
import InnerItem from './Component/InnerItem';
import { NavLink } from 'react-router-dom';
import ProductItem from './Component/ProductItem';
import _ from 'lodash';
import _uniq from 'lodash/uniq';
import _filter from 'lodash/filter';
import _intersection from 'lodash/intersection'
import './style.css';
import { connect } from 'react-redux';
import { handleInitProductTabs, handleInitProductInner } from './actions';
import Title from './Component/Title';
import Buttons from '../../Component/Button';
const TabItem = props => {
    const {ind, item, onClick, classActive, status } = props;
    return (
        <TabItems className={`tab-item ${classActive}`} status={status} onClick={onClick} >
            {item}
        </TabItems>
    );


}
const Home = props => {
    const {handleInitListProductInner, productReducer, handleInitListProductTabs} = props
    // lấy data trong productReducer (trong store)
    const {productTabs, listInnerProduct} = productReducer;
    const [state, setState] = useState({
        slider: [],
        status: true
    })
    // nút chọn các mặt hàng ở phần tabs
    const [filterTabs, setFilterTabs] = useState([]);
    // get data
    useEffect(() => {
        setState({
            ...state,
            slider: SliderItem,
        })
        handleInitListProductInner(ListInnerProduct);
        handleInitListProductTabs(ProductTabs)

    }, []);
    // xử lý carousel
    const settings = {
        dots: true,
        fade: true,
        infinite: true,
        speed: 500,
        slidesToShow: 1,
        slidesToScroll: 1,
        autoplay: true,
        speed: 500,
        autoplaySpeed: 2000,
        cssEase: "linear"
    }
    const listTabMenu = ['ALL','sản phẩm mới', 'sản phẩm bán chạy','sản phẩm phổ biến']
    // xử lý tabs carousel
    const Tabs = {
      infinite: true,
      slidesToShow: 4,
      slidesToScroll: 1,
      autoplay: true,
      speed: 500,
      autoplaySpeed: 2000,
      cssEase: "linear",
      responsive: [
        {
          breakpoint: 1200,
          settings: {
            slidesToShow: 3,
            slidesToScroll: 1,
          }
        },
        {
            breakpoint: 992,
            settings: {
              slidesToShow: 2,
              slidesToScroll: 1,
            }
          },
          {
            breakpoint: 575,
            settings: {
              slidesToShow: 1,
              slidesToScroll: 1,
            }
          },
        ]
    }
        // xử lý tabs products
    const [tabs, setTabs] = useState(0)
    const toggleTab = (id,item) => {
        setTabs(id);
        setState({
            ...state,
            status: true
        })
        const news = _uniq([item]);
        setFilterTabs(news)
    }
    // lay 30 san pham dau tien cua mang productTabs
    const newData = _.slice(productTabs,0,30);
    // 
    // lua chon theo type: giao cua 2 tap hop
    const filterProduct = () => {
        const data = newData.filter(item => {
            const {type} = item;
            const news = _intersection(filterTabs, type)
            const check = news.length ? true : false;
            return check
        })
        return data;
    }
    // trạng thái active của menu tabs
    const handleStateStatusTab = () => {
        setState({
            ...state,
            status: !state.status
        })
    }
    const data = filterTabs.length  ? filterProduct() : newData;
    const accessory = _filter(productTabs, item => item.tabs === 'accessory');
    // xu ly nut button cho phu kien
    const handleToAccessory = () => {
        const {history} = props;
        history.push("/phụ kiện khác")
    }
    // lấy sản phẩm giảm giá trong product-tabs
    const discount = _filter(productTabs, item => item.discount !== '');
    console.log('dis', discount);
    return (
        <HomeWrapper className="home">
            <div className="container">
                {/* xử lý banner */}
                <BannerWrapper className="banner">
                    <Slider {...settings}>
                        {state.slider.map((item, index) => {
                            return <BannerItem item={item} key={index} />
                        })}
                    </Slider>
                </BannerWrapper>
                <div className=" inner">
                    <div className="row">
                        {listInnerProduct.map(item => {
                            return <InnerItem location={props} item={item} key={item.id} />
                        })}
                    </div>
                </div>
                <ListProductWrapper className="list-product" status={state.status}>
                    <ul className="list-tab">
                        <li className="icon-tab" onClick={handleStateStatusTab}>
                            <span className="icon-tab-item">

                            </span>
                        </li>
                        {listTabMenu.map((item, ind) => {
                            return <TabItem status={state.status} item={item} key={ind} ind={ind} classActive={tabs === ind  ? 'active-tab' : ''} onClick={() => toggleTab(ind,item)} />
                        })}
                    </ul>
                    <div className="product tabs">
                        <Slider {...Tabs}>
                            {data.map((item, index) => {
                                return <ProductItem item={item} key={index} />
                            })}
           
                        </Slider>
                    
                    </div>
                    <div className="product accessory">
                        <Title name="phụ kiện khác" className="title-tab-product"/>
                        <div className="row">
                            {_.slice(accessory, 0,8).map(item => {
                                return <ProductItem item={item} key={item.id} className="col-lg-3 col-md-6 product-item"/>
                            })}
                        </div>
                        <Buttons name="Xem thêm" type="more" className="more" onClick={handleToAccessory}/>
                    </div>
                    <BannerDiscountWrapper className="banner-discount" bgImage="./assets/images/banner/banner-9.jpg">
                            <div className="info-banner-discount">
                                <img src="./assets/images/logo/1.png"/>
                                <div className="text-discount">
                                    <span className="text-while">
                                        khuyến mãi
                                    </span>
                                    <span className="text-yellow">
                                        giảm giá 50%
                                    </span>
                                </div>
                                <Buttons name="xem sản phẩm" type="more-product" className="btn-more" onClick={handleToAccessory}/>
                            </div>
                    </BannerDiscountWrapper>
                    <div className="product discount">
                        <Title name="sản phẩm giảm giá" className="title-tab-product"/>
                        <Slider {...sl}>
                        {_.slice(discount,0 , 8).map(item => {
                            return <ProductItem item={item} key={item.id} className="col-lg-3 col-md-6 product-item"/>
                        })}
                        </Slider>
                    </div>
                </ListProductWrapper>
            </div>

        </HomeWrapper>
    );
}
const mapStateToProps = state => {
    return {
        productReducer: state.products
    }
}
const mapDispatchToProps = dispatch => {
    return {
        handleInitListProductInner: data => dispatch(handleInitProductInner(data)),
        handleInitListProductTabs: data => dispatch(handleInitProductTabs(data))
    }
}
export default connect(mapStateToProps, mapDispatchToProps) (Home);
