import React from "react";
import { MessageCartWrapper } from "./style";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faTimes } from "@fortawesome/free-solid-svg-icons";
import Buttons from "../../../Component/Button";
import {formatMoney} from "./../../../Component/Format";
import ProductItem from "../../Home/Component/ProductItem";
import _ from "lodash";
const MessageCart = ({
  productCart,
  listProduct,
  history,
  handleClickCloseCart,
  counter,
  id
}) => {
  const {
    name,
    avatar,
    size,
    quantity,
    color, price,

  } = productCart;
  console.log('id==========', id);
  const formathPrice = formatMoney(price * counter);
  const body = document.querySelector("body")
  const handleClickToCart = () => {
    body.classList.remove("hidden")
    history.push("/gio-hang")
  }
   return (
    <MessageCartWrapper className="message-cart">
      <div className="message-content">
        <span className="close-message">
          <FontAwesomeIcon
          icon={faTimes}
          onClick={handleClickCloseCart}/>
        </span>
        <div
          className="content">
          <div className="product-to-cart">
            <p className="message-text">
              1 sản phẩm mới được thêm vào giỏ hàng của bạn
            </p>
            <div className="product-cart">
              <img src={avatar} alt="" onClick={handleClickToDetail}/>
              <div className="info">
                <h3 className="name-product">
                  {name}
                </h3>
                <p className="relate-info">
                  Nhóm màu: {color}, Size: {size}
                </p>
                <span className="price">
                  {formathPrice} đ
                </span>
                <div className="check">
              <span className="quantity">
                Số lượng: X{quantity}
              </span>
              <Buttons name="đến giỏ hàng" type="to" onClick={handleClickToCart}/>

            </div>
              </div>
            </div>

          </div>
        </div>
        <div className="container-fluid list-product">
          <div className="row">
            <h3 className="title">
              Bạn có thể quan tâm
            </h3>
          {_.map(_.slice(listProduct, 0, 4), item => {
              return <ProductItem
                className="col-lg-3 col-md-4 col-sm-6 col-6"
                key={item.id}
                item={item}
                nameButton="Xem thông tin"
                history={history}
                type="border"
              />
            })}
          </div>
        </div>
      </div>
    </MessageCartWrapper>
  );
}
export default MessageCart;
