import React, { useEffect, useState } from 'react';
import { HomeWrapper, BannerWrapper, ListProductWrapper, TabItems, CarouselProductWrapper, BannerDiscountWrapper, BodyLeftWrapper, BodyRightWrapper, ShoppingUnitItems, SellingProduct, HotlineWrapper, ProductTabWrapper, PolicyWrapper, CareWrapper, PostWrapper, BannerIntroMobileWrapper } from './style';
import Slider from 'react-slick';
import "slick-carousel/slick/slick.css";
import "slick-carousel/slick/slick-theme.css";
import { SliderItem, ListInnerProduct, ProductTabs, ShippingUnits, Polices, News } from './data';
import BannerItem from './Component/BannerSlider';
import InnerItem from './Component/InnerItem';
import { NavLink } from 'react-router-dom';
import ProductItem from './Component/ProductItem';
import _, { random } from 'lodash';
import _uniq from 'lodash/uniq';
import _filter from 'lodash/filter';
import _intersection from 'lodash/intersection'
import './style.css';
import { connect } from 'react-redux';
import { handleInitProductTabs, handleInitProductInner } from './actions';
import Title from './Component/Title';
import Buttons from '../../Component/Button';
import TitlePage from '../../Component/Title';
import BannerImageItem from './Component/BannerImageItem';
import SellingItem from './Component/SellingItem';
import Button from './../../Component/Button'
import PolicyItem from './Component/PolicyItem';
import PostItem from './Component/Post';
const TabItem = props => {
    const { ind, item, onClick, classActive, status } = props;

    return (
        <TabItems className={`tab-item ${classActive}`} status={status} onClick={onClick} >
            {item}
        </TabItems>
    );


}
export const ShoppingUnitItem = ({ shoppingUnitItem: { name } }) => {
    return (
        <ShoppingUnitItems className="shopping-unit-item">
            <img src={name} alt="shopping unit" />
        </ShoppingUnitItems>
    );
}
const Home = props => {
    const { handleInitListProductInner, productReducer, handleInitListProductTabs, history } = props
    // lấy data trong productReducer (trong store)
    const { productTabs, listInnerProduct } = productReducer;
    const [state, setState] = useState({
        slider: [],
        shippingUnits: [],
        status: true,
        listPolicy: [],
        news: []
    })
    // nút chọn các mặt hàng ở phần tabs
    const [filterTabs, setFilterTabs] = useState([]);

    // xử lý loading
    const [isLoading, setIsLoading] = useState(true);
    // get data
    useEffect(() => {
        setIsLoading(true);
        setTimeout(() => { setIsLoading(false) }, 3000)
        setState({
            ...state,
            slider: SliderItem,
            shippingUnits: ShippingUnits,
            listPolicy: Polices,
            news: News
        })
        handleInitListProductInner(ListInnerProduct);
        handleInitListProductTabs(ProductTabs)
        window.scrollTo({
            top: 0,
            behavior: 'smooth'
        })
    }, []);
    // xử lý carousel
    const settings = {
        dots: true,
        fade: true,
        infinite: true,
        speed: 500,
        slidesToShow: 1,
        slidesToScroll: 1,
        autoplay: true,
        speed: 500,
        autoplaySpeed: 2000,
        cssEase: "linear"
    }
    const listTabMenu = ['sản phẩm mới', 'sản phẩm bán chạy', 'sản phẩm phổ biến']
    // xử lý tabs carousel

    const productTab = {
        infinite: true,
        slidesToShow: 4,
        slidesToScroll: 1,
        autoplay: true,
        speed: 1000,
        autoplaySpeed: 2000,
        cssEase: "linear",
        responsive: [
            {
                breakpoint: 1200,
                settings: {
                    slidesToShow: 3,
                    slidesToScroll: 1,
                }
            },
            {
                breakpoint: 992,
                settings: {
                    slidesToShow: 3,
                    slidesToScroll: 1,
                }
            },
            {
                breakpoint: 768,
                settings: {
                    slidesToShow: 2,
                    slidesToScroll: 1,
                }
            },
            {
                breakpoint: 575,
                settings: {
                    slidesToShow: 1,
                    slidesToScroll: 1,
                }
            },
        ]
    }
    const sliderProduct = {
        infinite: true,
        slidesToShow: 1,
        slidesToScroll: 1,
        autoplay: true,
        speed: 1000,
        autoplaySpeed: 2000,
        cssEase: "linear",
        responsive: [
            {
                breakpoint: 1200,
                settings: {
                    slidesToShow: 3,
                    slidesToScroll: 1,
                }
            },
            {
                breakpoint: 992,
                settings: {
                    slidesToShow: 3,
                    slidesToScroll: 1,
                }
            },
            {
                breakpoint: 776,
                settings: {
                    slidesToShow: 2,
                    slidesToScroll: 1,
                }
            },
            {
                breakpoint: 576,
                settings: {
                    slidesToShow: 1,
                    slidesToScroll: 1,
                }
            },
        ]
    }

    const SellingSlider = {
        infinite: true,
        slidesToShow: 1,
        slidesToScroll: 1,
        autoplay: true,
        speed: 1000,
        autoplaySpeed: 2000,
        cssEase: "linear",
        responsive: [
            {
                breakpoint: 1200,
                settings: {
                    slidesToShow: 1,
                    slidesToScroll: 1,
                }
            },
            {
                breakpoint: 992,
                settings: {
                    slidesToShow: 3,
                    slidesToScroll: 1,
                }
            },
            {
                breakpoint: 768,
                settings: {
                    slidesToShow: 1,
                    slidesToScroll: 1,
                }
            },
        ]
    }
    // xử lý tabs products
    const [tabs, setTabs] = useState(0)
    const toggleTab = (id, item) => {
        setTabs(id);
        setState({
            ...state,
            status: true
        })
        const news = _uniq([item]);
        setFilterTabs(news)
    }
    // lay 30 san pham dau tien cua mang productTabs
    const newData = _.slice(productTabs, 0, 30);
    //
    // lua chon theo type: giao cua 2 tap hop
    const filterProduct = () => {
        const data = newData.filter(item => {
            const { type } = item;
            const news = _intersection(filterTabs, type)
            const check = news.length ? true : false;
            return check
        })
        return data;
    }
    // trạng thái active của menu tabs
    const handleStateStatusTab = () => {
        setState({
            ...state,
            status: !state.status
        })
    }
    const data = filterTabs.length ? filterProduct() : newData;
    const accessory = _filter(productTabs, item => item.tabs === 'accessory');
    // xu ly nut button cho phu kien
    const handleToAccessory = () => {
        const { history } = props;
        history.push("/phụ kiện khác")
    }
    // lấy sản phẩm giảm giá trong product-tabs
    const discount = _filter(productTabs, item => item.discount !== '');
    const clothesProduct = _filter(productTabs, item => item.tabs === "clothes");
    const shoesProduct = _filter(productTabs, item => item.tabs === "sneaker");
    const accessoryProduct = _filter(productTabs, item => item.tabs === "accessory");
    let productNew = [];
    productNew = [
        ...productNew,
        { id: 1, titleTabs: 'quần áo', product: clothesProduct },
        { id: 2, titleTabs: 'giày dép', product: shoesProduct },
        { id: 3, titleTabs: 'các phụ kiện khác', product: accessoryProduct }
    ];
    const slices = state.slider.slice(0, 2);

    // xử lý selling unit
    let sellingUnit = [];
    _.forEach(productTabs, item => {
        sellingUnit = [...sellingUnit, productTabs[Math.floor(Math.random() * productTabs.length)
        ]]
    })

    // link tới trang giới thiệu
    const handleClickToIntro = () => {
        history.push("/gioi-thieu")
    }


    // xử lý load
    if (isLoading) return <div className="loading" style={{
        width: "100%",
        height: "100vh",
        overflow: 'hidden'
    }}>
        <img
            style={{
                margin: '5rem auto',
                display: 'block',
                width: '20%'
            }}
            src="/assets/images/logo/load.gif"
            alt="loading" />
    </div>
    return (
        <HomeWrapper className="home">
            <TitlePage name="Trang Chủ" />
            <BannerWrapper className="banner">
                <div className="container">
                    <div className="slider">
                        <Slider {...settings}>
                            {state.slider.map((item, index) => {
                                return <BannerItem item={item} key={item.id} />
                            })}
                        </Slider>
                    </div>
                    <ul className="banner-right">
                        {state.slider.slice(0, 2).map(item => {
                            return <BannerImageItem type="banner-right" bannerImageItem={item} key={item.id} />
                        })}
                    </ul>
                </div>
            </BannerWrapper>
            <div className=" body-wrapper">
                {/* xử lý body */}

                <div className="container">
                    <BodyLeftWrapper className="body-left">
                        <ul className="list-shopping-unit">
                            {state.shippingUnits.map(item => <ShoppingUnitItem shoppingUnitItem={item} key={item.id} />)}
                        </ul>
                        <SellingProduct className="selling-product">
                            <h3 className="title">
                                Sản phẩm bán chạy
                            </h3>
                            <Slider {...SellingSlider}>
                                <div>
                                    {_.map(_.uniqBy(_.slice(sellingUnit, 0, 6), 'id'), item => {
                                        return <SellingItem sellingItem={item} key={item.id} />
                                    })}
                                </div>
                                <div>
                                    {_.map(_.uniqBy(_.slice(sellingUnit, 6, 12), 'id'), item => {
                                        return <SellingItem sellingItem={item} key={item.id} />
                                    })}
                                </div>
                            </Slider>
                        </SellingProduct>
                        <HotlineWrapper className="hotline">
                            <div className="hotline-content">
                                <h3 className="title">
                                    hotline
                                </h3>
                                <p className="phone">
                                    039.2272.154 0376326810
                                </p>
                            </div>
                        </HotlineWrapper>
                    </BodyLeftWrapper>
                    <BodyRightWrapper className="body-right">
                        <h3 className="title-body">
                            sản phẩm nổi bật
                        </h3>
                        <div className="product">
                            <div className="slider-product">
                                <Slider {...sliderProduct}>
                                    {_.map(_.uniqBy(_.slice(sellingUnit, 0, 8), 'id'), item => {
                                        return <ProductItem nameButton="xem thông tin" item={item} key={item.id} />
                                    })}
                                </Slider>
                            </div>
                            <div className="list-product container-fluid">
                                <div className="row">
                                    {_.map(_.uniqBy(_.slice(sellingUnit, 0, 2), 'id'), item => {
                                        return <ProductItem typeHot="hot" type="border" className="col-xl-5 col-lg-5 col-md-5 col-sm-5" nameButton="xem thông tin" item={item} key={item.id} />
                                    })}
                                    <div className="btn-to col-lg-12">
                                        <Button name="Xem tất cả sản phẩm" />
                                    </div>
                                </div>
                            </div>
                            <ProductTabWrapper className="product-tab">
                                <div className="header-tab">
                                    <h3 className="title-body">
                                        hàng mới về
                                    </h3>
                                    <ul className="list-tab">
                                        {listTabMenu.map((item, ind) => {
                                            return <TabItem status={state.status} item={item} key={ind} ind={ind} classActive={tabs === ind ? 'active-tab' : ''} onClick={() => toggleTab(ind, item)} />
                                        })}
                                    </ul>

                                </div>
                                <div className="list-product-tabs ">
                                    <Slider {...productTab}>
                                        {data.map((item, index) => {
                                            return <ProductItem type="border" nameButton="xem thông tin" icon={item.discountContent} item={item} key={item.id} />
                                        })}

                                    </Slider>

                                </div>
                                <div className="banner-intro" onClick={handleClickToIntro}>

                                    <div className="intro">
                                        <p className="intro-content">
                                            Chào mừng quý khách đến với <span>S</span>HOPPANDA.NET.VN
                                        </p>
                                    </div>
                                    <img src="/assets/images/logo/6.png" alt="logo" />
                                </div>
                            </ProductTabWrapper>
                        </div>
                    </BodyRightWrapper>
                </div>

                <BannerIntroMobileWrapper>
                    <div className="banner-intro" onClick={handleClickToIntro}>

                        <div className="intro">
                            <p className="intro-content">
                                Chào mừng quý khách đến với <span>S</span>HOPPANDA.NET.VN
                            </p>
                        </div>
                        <img src="/assets/images/logo/6.png" alt="logo" />
                    </div>
                </BannerIntroMobileWrapper>


                <PolicyWrapper className="policy-wrapper">
                    <div className="container">
                        <h3 className="title-body">
                            Chính sách & hướng dẫn của SHOPPANDA
                        </h3>
                        <div className="row">
                            {state.listPolicy.map(item => <PolicyItem className="col-lg-6 col-md-12" policyItem={item} key={item.id} />)}
                        </div>
                    </div>
                </PolicyWrapper>
                <CareWrapper className="care-wrapper">
                    <div className="container">
                        <h3 className="title-body">
                            Có thể bạn quan tâm
                        </h3>
                        <p className="descript-care">
                            Bạn có thể tìm thấy những phẩm tốt và chi phí được giảm tới 70% với những mẫu mã đa dạng và phù hợp với hầu bao cả các lứa tuổi hiện nay. Chúng tôi cam kết luôn mang đến cho các bạn những sản phẩm tốt nhất, chất lượng nhất.
                        </p>
                        <Slider {...productTab}>
                            {_.map(_.uniqBy(_.slice(sellingUnit, 0, 8), 'id'), item => {
                                return <ProductItem type="border" nameButton="xem thông tin" item={item} key={item.id} />
                            })}

                        </Slider>
                    </div>
                </CareWrapper>
                <PostWrapper className="post-wrapper">
                    <div className="container">
                        <h3 className="title-body">
                            Bài viết mới nhất
                        </h3>
                        <p className="descript-post">
                            Blog tập hợp những bản tin, bài viết về kinh nghiệm mua sắm và chia sẻ các cung bậc cảm xúc hằng ngày. Tập hợp những mẹo vặt để cải thiện của sống của bạn tốt hơn, hoàn hảo hơn.
                        </p>
                        <div className="row">
                            {_.map(_.slice(state.news, 0, 3), item => {
                                return <PostItem className="col-lg-4 col-md-12" postItem={item} key={item.id} />
                            })}
                        </div>
                        <Button name="Xem thêm bài viết" type="add" />
                    </div>
                </PostWrapper>
            </div>

        </HomeWrapper>
    );
}
const mapStateToProps = state => {
    return {
        productReducer: state.products
    }
}
const mapDispatchToProps = dispatch => {
    return {
        handleInitListProductInner: data => dispatch(handleInitProductInner(data)),
        handleInitListProductTabs: data => dispatch(handleInitProductTabs(data))
    }
}
export default connect(mapStateToProps, mapDispatchToProps)(Home);
