import React from "react";
import { useState } from "react";
import { NavLink } from "react-router-dom";
import { MenuItems, SubMenuItems } from "./style";
export const SubMenuItem = ({ subMenuItem, setStatus }) => {
  const { name, path } = subMenuItem;
  const handleChangeStatus = setState => {
    setState(true);
  }
  return (
    <SubMenuItems className="sub-menu-item">
      <NavLink
        onClick={() => handleChangeStatus(setStatus)}
        className="sub-menu-link"
        activeClassName="active"
        to={path}
        exact>
        {name}
      </NavLink>
    </SubMenuItems>
  );
}
const MenuItem = ({ menuItem, type }) => {
  const { name, path, icon, subMenu } = menuItem;
  const [status, setStatus] = useState(true);
  const handleChangeStatus = () => {
    setStatus(!status);
  }
  return (
    <MenuItems
      className="menu-item"
      status={status}
      type={type}
      subMenu={subMenu}>
      <NavLink
        to={path}
        exact
        className="menu-link"
        activeClassName="active"
      >
        {icon}
        {name}
      </NavLink>
      {subMenu && <ul className="sub-menu">
        {subMenu.map(item => {
          return <SubMenuItem setStatus={setStatus} subMenuItem={item} key={item.id} />
        })}
      </ul>}
    </MenuItems>
  );
}
export default MenuItem;
