import React, { useState } from 'react';
import { RegisterWrapper } from './style';
import Input from './../input';
import TitlePage from '../Title';
import { NavLink } from 'react-router-dom';
import { useEffect } from 'react';
const Register = props => {
    const [state, setState] = useState({
        username: '',
        pass: '',
        repass: ''
    })
    useEffect(() => {
        window.scrollTo({
            top: 0,
            behavior: 'smooth'
          })
    }, [])
    const [type, setType] = useState('password')
    const handleChangeCheck = () => {
        setType(type === "password" ? 'text' : 'password');
    }
    const handleChangeInput = event => {
        const { type, name, value } = event.target;
        setState({
            [name]: value
        })


    }

    return (
        <RegisterWrapper className="form-login-register register" bgImage="./assets/images/register/clothes1.jpg" backgroundImage="./assets/images/register/clothes2.jpg">
            <TitlePage name="Đăng Ký"/>
            <div className="container">
                <form action="" className="form-login-register">
                    <h3 className="name-form">
                        Đăng ký
                    </h3>
                    <Input onChange={handleChangeInput} id="user" type="text" value={state.username} name="username" placeholder="Tên đăng nhập..." />
                    <Input onChange={handleChangeInput} id="pass" type={type} value={state.pass} name="pass" placeholder="Mật khẩu..." />
                    <Input onChange={handleChangeInput} id="pass" type={type} value={state.repass} name="repass" placeholder="Nhập lại mật khẩu..." />
                    <div className="forgot-pass">
                        <div className="checkbox">
                            <Input id="showPass" onChange={handleChangeCheck} type="checkbox" name="showPass" value="" nameLabel="Hiển thị mật khẩu" />
                        </div>
                        <NavLink exact to="/login" className="have-acount">Có tài khoản</NavLink>
                    </div>
                    <Input type="submit" value="ĐĂNG KÝ" name="submit" />


                </form>

            </div>
        </RegisterWrapper>
    );
}
export default Register;
