import React from "react";
import { LeftPostDetailWrapper, MainPostDetailWrapper, PostDetailWrapper } from "./style";
import {
  getPostDetailRequest,
  getListPostRequest
} from "./actions";
import { connect } from "react-redux";
import { useEffect } from "react";
import {
  selectPostDetail,
  selectPosts,
  selectIsLoading,
  selectError
} from "./selector"
import Loading from "../Components/Loading";
import PostCareItem from "./PostCareItem";
import _ from 'lodash'
const PostDetail = props => {
  const {
    match: { params: { id } },
    handleGetPostDetail,
    postDetail,
    posts,
    isLoading,
    error,
    handleGetListPost
  } = props;
  useEffect(() => {
    handleGetPostDetail(+id);
    handleGetListPost()
  }, []);
  if (isLoading) return <Loading />
  const {
    avatar,
    createdAt,
    description,
    name
  } = postDetail;
  const listPost = [];
  forEach()
  return (
    <PostDetailWrapper className="post-detail">
      <div className="container">
        <MainPostDetailWrapper className="main-post-detail">
          <img src={avatar} alt="" />
          <h3 className="name-post">
            {name}
          </h3>
          <p className="time">
            <span>Shoppanda</span>
            <span className="time-item">
              {createdAt.slice(0, 10)}
            </span>
          </p>
          {description.map(item => {
            const { name, desc, type , id} = item;
            return (
              !type ? <p className="descript" key={id}>
                {name}
              </p> :
                (type === "heading" ?
                  <h5
                  className="indexing"
                  key={id}
                  >
                    {name}
                  </h5> :
                  <div
                  className="image-post"
                  key={id}
                  >
                    <img src={name} alt="name" />
                    <p className="desc-image">
                      {desc}
                    </p>
                  </div>)
            );
          })}
        </MainPostDetailWrapper>
        <LeftPostDetailWrapper className="left-post-detail">
          <h3 className="title">
            có thể bạn quan tâm
          </h3>
          <div className="list-post-care">
            {posts.map(item => {
              return <PostCareItem postCareItem={item} key={item.id}/>
            })}
          </div>
        </LeftPostDetailWrapper>
      </div>
    </PostDetailWrapper>
  );
}
const mapStateToProps = state => {
  return {
    postDetail: selectPostDetail(state),
    posts: selectPosts(state),
    isLoading: selectIsLoading(state),
    error: selectError(state)
  }
}
const mapDispatchToProps = dispatch => {
  return {
    handleGetPostDetail: data => dispatch(getPostDetailRequest(data)),
    handleGetListPost: () => dispatch(getListPostRequest())
  }
}
export default connect(mapStateToProps, mapDispatchToProps)(PostDetail);
