import {
  GET_LIST_PRODUCT_DETAIL_REQUEST,
  GET_LIST_PRODUCT_DETAIL_SUCCESS,
  GET_LIST_PRODUCT_DETAIL_FAILED,
  HANDLE_INCREMENT,
  HANDLE_DECREMENT
} from './contants';
const initialState = {
  detail: {},
  isLoading: true,
  error: [],
  counter: 0
}
const detailReducer = (state = initialState, action) => {
  switch(action.type) {
    case GET_LIST_PRODUCT_DETAIL_REQUEST:
      return {
        ...state,
        isLoading: true
      }
    case GET_LIST_PRODUCT_DETAIL_SUCCESS:
      return {
        ...state,
        isLoading: false,
        detail: action.data
      };
    case GET_LIST_PRODUCT_DETAIL_FAILED:
      return {
        ...state,
        isLoading: false,
        error: "abc"
      };
    case HANDLE_INCREMENT:
      return {
        ...state,
        counter: state.counter + 1
      }
     case HANDLE_DECREMENT:
       return {
         ...state,
         counter: state.counter > 0 && state.counter - 1
       }
    default:
      return state;
  }
}
export default detailReducer;
