import styled from "styled-components";
export const SearchProductItems = styled.div`
    display: flex;
    align-items: center;
    @media (max-width: 576px) {
        flex-direction: column;
    }
    & > img {
        width: 30%;
        cursor: pointer;
        transform: scale(.85);
        transition: .3s;
        &:hover {
            transform: scale(1);
        }
    }
    .info {
        display: flex;
        flex-wrap: wrap;
        flex: 1;
        .name-product {
            width: 100%;
            font-size: 1.2rem;
            padding: .25rem .75rem .25rem 0;
        }
        .discount {
            color: #cacaca;
            margin-right: .35rem;
            text-decoration: line-through;
        }
        .price {
            color: red;
            font-size: 1.5rem;
        }
    }
`
