import React from "react";
import { SearchProductItems } from "./style";
const SearchProductItem = props => {
    const { item, onClick, empty } = props;
    const { name, price, discount, avatar } = item;
    const priceFormat = new Intl.NumberFormat('en-US', {
        style: 'decimal',
        currency: 'USD',
    }).format(price);
    const discountFormat = new Intl.NumberFormat('en-US', {
        style: 'decimal',
        currency: 'USD',
    }).format(discount);
    return (
        <SearchProductItems className="search-product-item" discount={discount} onClick={onClick}>
            <img src={avatar} alt={name} />
            <div className="info">
                <div className="title-text">
                    <h4 className="name-product">
                        {name}
                    </h4>
                </div>
                <span className="discount">{discountFormat}</span>
                <span className="price">{priceFormat}</span>
            </div>
        </SearchProductItems>
    );
}
export default SearchProductItem;
