import styled from "styled-components";
export const FacebookWrapper = styled.div`
    text-align: center;
    background: red;
    width: max-content; 
    position: relative;
    z-index: 3;
    &:after {
        width: 100%;
        height: 100%;
        position: absolute;
        top: 0;
        left: 0;
        background: #4c69ba;
        content: 'Facebook';
        display: flex;
        justify-content: center;
        align-items: center;
        color: #fff;
        font-size: 1.5rem;

    }
    & > span button {
        z-index: 3;
        position: relative;
        opacity: 0;
        padding: 0.75rem .5rem;
    }
`
