import styled from "styled-components";
export const HeaderWrapper = styled.div`
    .header-top {
        width: 100%;
        display: flex;
        justify-content: space-between;
        align-items: center;
        padding: .25rem 3rem;
        background: #020202;
        & > img {
            width: 20%;
            cursor: pointer;
        }
        .group-card {
            display: flex;
            border: 1px solid #fff;
            position: relative;
            width: 20%;
            .search {
          
                .icon-search {
                    padding: 0.25rem 0.35rem;
                    margin-right: .35rem;
                    color: #fff;
                }
                & > .input-search {
                    position: absolute;
                    bottom: 0;
                    
                    background: red;
                    padding: 2rem;
                    width: 100%;
                }
            }
            .card {
                display:flex;
                flex-direction: row;
                align-items: center;
                border: 3px solid red;
              
            }
        }
    }
`
