import React, { useEffect, useState } from "react";
import {
  getListProductDetailRequest
} from "./action";
import { connect } from "react-redux";
import {
  selectDetail,
  selectIsLoading
} from "./selector"
import Loading from "../Components/Loading";
import { DetailWrapper, ProductDetailWrapper } from "./style";
import NamePage from "../Components/NamePage";

const Detail = (props) => {
  const {
    handleGetListProductDetail,
    history,
    match: { params: { id } },
    detail,
    isLoading
  } = props;
  const [index, setIndex] = useState(0);
  useEffect(() => {
    handleGetListProductDetail(+id)
  }, [])
  if (isLoading) return <Loading />
  // hiển thị info
  const {
    avatar,
    color,
    description,
    discount,
    discountContent,
    evaluate,
    like,
    name,
    price,
    promotion,
    quantity,
    questionAnswer,
    ship,
    size,
  } = detail;
  const handleClickToHome = () => {
    history.push("/");
  }
  const {brand, descriptContent, subImage} = description;
  const selectImage = subImage[0];
  console.log('sub', subImage);
  return (
    <DetailWrapper className="detail-wrapper">
      <div className="container">
        <ProductDetailWrapper className="product-detail">
          <div className="slider-images">
            <img src={selectImage.name} alt={selectImage.name}/>
          </div>
          <div className="select-product">

          </div>
          <div className="select-delivery">

          </div>
        </ProductDetailWrapper>
      </div>
    </DetailWrapper>
  );
}
const mapStateToProps = state => {
  return {
    isLoading: selectIsLoading(state),
    detail: selectDetail(state)
  }
}
const mapDispatchToProps = dispatch => {
  return {
    handleGetListProductDetail: data => dispatch(getListProductDetailRequest(data))
  }
}
export default connect(mapStateToProps, mapDispatchToProps)(Detail);
