import styled from 'styled-components';
export const DetailWrapper = styled.div`

`
export const ProductDetailWrapper = styled.div`
  display: flex;
  width: 100%;
  .slider-images,.select-delivery {
    width: 25%;
    padding: 1.2rem 1rem;
    border: 1px solid;
  }
  .select-product {
    flex: 1;
    border: 1px solid;
  }
  .slider-images {
    & > img {
      width: 100%;
      cursor: pointer;
      transform: scale(.9);
      transition: .3s;
      &:hover {
        transform: scale(1);
      }
    }
    .indicator {
      display: flex;
      flex-wrap: wrap;
      padding: 2rem .25rem !important;
      margin: 0;
      border-top: 1px solid #cacaca;
    }
  }
`
export const IndicatorItems = styled.li`
  width: calc((100% - 2.5rem)/3);
  margin: .45rem .5rem.45rem 0;
  border: 1px solid red;
  cursor: pointer;
  & > img {
    width: 100%;
    object-fit: cover;
  }
`
