import React from "react";
import { HeadTabWrapper } from "./style";
import NamePage from "../NamePage";
import SelectOption from "../SelectOption";
const HeadTabs = props => {
    const {nameLink, to, name, footWear} =  props;
      // onChange select
      const [select, setSelect] = useState(0)
      // sort by name
      const [listProductSortBy, setListProductSortBy] = useState([]);
      const compareByName = (obFist, obSecond) => {
          if(obFist.name > obSecond.name) {
              return 1
          }
          else if(obFist.name < obSecond.name) {
              return -1
          }
          return 0
      }
      const handleSelectChange = event => {
          const {value} = event.target;
          setSelect(+value)
          console.log('value', value);
          let newListProduct = [];
          if(+value === 1) {
              newListProduct = [...(listProductSortBy.length ? listProductSortBy : footWear)].sort(compareByName);
          }
          else if(+value === 2) {
              newListProduct = _.reverse( [...(listProductSortBy.length ? listProductSortBy : footWear)].sort(compareByName));
          }
          else {
              newListProduct = footWear;
          }
          setListProductSortBy(newListProduct)
      }
      const getListProductByFilter = () => {
          if(listProductSortBy.length) {
              return listProductSortBy;
          }
          return footWear;
      }
    return (
        <HeadTabWrapper>
            <NamePage nameLink={nameLink} to={to} name={name} />
            <SelectOption nameSort="Sắp xếp theo" value={select} handleSelectChange={handleSelectChange} nameSelect="Name" option={Name} />
        </HeadTabWrapper>
    );
}
export default HeadTabs;
