import React, {useEffect, useState} from 'react';
import ReactPaginate from 'react-paginate';
import { PaginationPageWrapper } from './style';
import ProductItem from '../../../Home/Component/ProductItem';
import _slice from 'lodash/slice'
const PaginationPage = props => {
    const {product} = props;
    const productLength = product.length;
    console.log('length', productLength );
    useEffect(() => {
        const [page, setPage] = useState(_slice(product,0, product.length + 1));
        console.log('page,', page);
        const [pageNumber, setPageNumber] = useState(0);
        const productsPerPage = 10;
        const pagesVisited  = pageNumber * productsPerPage;
        console.log('page', page);
        const pageCount = Math.ceil(page.length / productsPerPage);
        const changePage = (data) => {
            const {selected} = data
            console.log('data', data);
            setPageNumber(selected);
            console.log('page22',pageNumber);
        }
    },[])
    return(
        <PaginationPageWrapper className="row">
             {page.slice(pagesVisited,pagesVisited + productsPerPage).map(item => {
                        return <ProductItem item={item} key={item.id} className="col-lg-3 col-md-6 product-item"/>
                    })}
                    <ReactPaginate
                        previousLabel={"<<"}
                        nextLabel={">>"}
                        pageCount={pageCount}
                        breakLabel={'...'}
                        onPageChange={changePage}
                        containerClassName={"paginationBttns"}
                        previousLinkClassName={"previousBttn"}
                        nextLinkClassName={"nextBttn"}
                        disabledClassName={"paginationDisabled"}
                        activeClassName={"paginationActive"}    
                    ></ReactPaginate>
        </PaginationPageWrapper>
    );
}
export default PaginationPage;
