import styled from "styled-components";
export const HomeWrapper = styled.div`
    padding-bottom: 2.5rem;
`
export const BannerWrapper = styled.div`
    width: 100%;
    height: 70vh;
    overflow: hidden;
    .slick-prev {
        left: 0;
        z-index: 7;
        &:before {
            width: 50px;
            height: 50px;
            opacity: 1;
            content: '\f053';
            position: absolute;
            top: 0;
            left: 0;
            font-family: 'Fontawesome' !important;
            display: flex;
            justify-content: center;
            align-items: center;
            color: #000;
            font-size: 2.5rem;
        }
    }
    .slick-next {
        right: 0;
        
    }

`
