import React from "react";
import { HeaderBottomItems,DropdownMenuWrapper,DropdownMenuitems } from "./style";
import { NavLink } from "react-router-dom";
const DropdownMenuItem = props => {
    const {item,parent} = props;
    console.log('pa', parent);
    return(
        <DropdownMenuitems className="dropdown-menu-item">
            <NavLink to={`/${parent}/${item}`}>{item}</NavLink>
        </DropdownMenuitems>
    );
}
const HeaderBottomItem = props => {
    const  {item} = props;
    const {name, dropdown} = item;
    console.log('drop', dropdown);
    return(
        <HeaderBottomItems className="menu-item">
            <NavLink exact activeClassName="active-menu" className="item-link" to={item.name === 'trang chủ' ? '/' : `/${name}`}>  {item.name} </NavLink>
            {dropdown=== ['']  ? <span>abc</span> : <DropdownMenuWrapper className="dropdown-menu">
                {dropdown.map((item, index) => {
                    return <DropdownMenuItem parent={name} item = {item} key = {index}/>
                })}
            </DropdownMenuWrapper>}
        </HeaderBottomItems>
    );
}
export default HeaderBottomItem;
