import styled from 'styled-components';
export const DetailWrapper = styled.div`
  & > .container {
    width: 100%;
  }
`
export const ProductDetailWrapper = styled.div`
  .slider-images,.select-delivery {
    width: 20%;
    padding: 1.4rem 1rem;
    height: 300px;
    border: 1px solid;
  }
  .select-product {
    flex: 1;
    border: 1px solid;
  }
`
