import React from 'react';
import { ProductItems } from './style';
import Buttons from './../../../../Component/Button'
const ProductItem = props => {
    const { item, className , icon} = props;
    const { name, price, avatar, discount } = item;
    const priceFormat = new Intl.NumberFormat('en-US', {
        style: 'decimal',
        currency: 'USD',
    }).format(price);
    const discountFormat = new Intl.NumberFormat('en-US', {
        style: 'decimal',
        currency: 'USD',
    }).format(discount);
    return (
        <ProductItems discount={discount} className={className}>
            <span className="icon-discount"></span>
            <img src={avatar} alt={name} />
            <p className="title-product">
                <h4 className="name-product">{name}</h4>
            </p>
            <div className="price">
                <span className="discount">{discountFormat}</span>
                <span className="price-item">{priceFormat}</span>
            </div>
            <Buttons name="Thêm vào giỏ hàng" type="card" className="btn-card" />
        </ProductItems>
    );
}
export default ProductItem;
