import {
  call,
  takeEvery,
  takeLatest,
  all,
  put,
  delay,
  fork
} from 'redux-saga/effects';
import {
  getListPostSuccess,
  getPostDetailSuccess,
  getListPostFailed,
  getPostDetailFailed
} from "./actions";
import {
  GET_LIST_POST_REQUEST,
  GET_POST_DETAIL_REQUEST
} from "./contants";
import {
  getListPostApi,
  getPostIdApi
} from "./service";
function* getPostDetailProcess(params) {
  try {
    console.log("params========", params);
    // const respon = yield call(get)
  }catch (err) {}
}
function* watchGetPostDetail() {
  yield takeLatest(GET_POST_DETAIL_REQUEST, getPostDetailProcess);
}
function* watchAll() {
  yield all([
    watchGetPostDetail()
  ])
}
export default watchAll;
