import React, { useState } from 'react';
import { LoginWrapper } from './style';
import Input from './../input';
const Login = props => {
    const [state,setState] = useState({
        username: '',
        pass: '',
    })
    const [type, setType] = useState('password')
    const handleChangeCheck = () => {
        setType(type === "password" ? 'text' : 'password');
    }
    const handleChangeInput = event => {
        const {type,name, value} = event.target;
        setState({
            [name]: value
        })
        
   
    }
   
    return (
        <LoginWrapper className="form-login-register login">
            <div className="container">
                <h3 className="name-form">
                    Đăng nhập
                </h3>
                <form className="form" action="" >
                    <Input className="form-control" type="text" id="username" nameLabel="Username:" onChange={handleChangeInput} name="username" value={state.username} placeholder="Username..." />
                    <Input className="form-control" type={type} id="pass" nameLabel="Password:" onChange={handleChangeInput} name="pass" value={state.pass} placeholder="Password..." />
                    <div className="submit-form">
                        <Input className="form control btn btn-success" name="submit" value="Login" type="submit" />
                        <div className="checkbox-rememberPass">
                            <Input type="checkbox" onChange={handleChangeCheck}  nameLabel="Hiển thị mật khẩu" name="" value=""/>
                        </div>
                    </div>
                </form>
            </div>
        </LoginWrapper>
    );
}
export default Login;
